mapper.insert:
insert into t_bank_info (ID, BANK_NAME, SUPPORT_UNIONPAY, 
  UNIONPAY_CODE, ENABLED, CREATE_ID, 
  CREATE_TIME, UPDATE_ID, UPDATE_TIME
  )
values (#{id,jdbcType=VARCHAR}, #{bankName,jdbcType=VARCHAR}, #{supportUnionpay,jdbcType=BIT}, 
  #{unionpayCode,jdbcType=VARCHAR}, #{enabled,jdbcType=BIT}, #{createId,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateId,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select * from t_bank_info
where 1=1 

	and ENABLED =#{enabled}
