mapper.insert:
insert into t_car_style (ID, CAR_SERIAL_ID, CAR_STYLE_NAME, 
  DISPLACEMENT, GUIDE_PRICE, INDEX_STR
  )
values (#{id,jdbcType=VARCHAR}, #{carSerialId,jdbcType=VARCHAR}, #{carStyleName,jdbcType=VARCHAR}, 
  #{displacement,jdbcType=DOUBLE}, #{guidePrice,jdbcType=DOUBLE}, #{indexStr,jdbcType=VARCHAR}
  )

select.if:
		select * from t_car_style style
 		left join t_car_serial serial on style.CAR_SERIAL_ID = serial.id
 		where 1=1

 		and style.CAR_SERIAL_ID = #{param.carSerialId}


 		and serial.CAR_BRAND_ID = #{param.carBrandId}

mapper.select:

select a.* from t_car_brand a 
left join t_car_serial b on a.id = b.car_brand_id 
where b.id = #{CAR_SERIAL_ID}
