mapper.insert:
insert into t_remission_item (ID, APPLY_TYPE, APPLY_REF_ID, 
  CAPITAL, INTEREST, OVERDUE_AMOUNT, 
  OTHER_FEE, OTHER_OVERDUE_AMOUNT, LATE_FEE
  )
values (#{id,jdbcType=VARCHAR}, #{applyType,jdbcType=VARCHAR}, #{applyRefId,jdbcType=VARCHAR}, 
  #{capital,jdbcType=DOUBLE}, #{interest,jdbcType=DOUBLE}, #{overdueAmount,jdbcType=DOUBLE}, 
  #{otherFee,jdbcType=DOUBLE}, #{otherOverdueAmount,jdbcType=DOUBLE}, #{lateFee,jdbcType=DOUBLE}
  )

mapper.select:
select * from t_remission_item
where APPLY_TYPE = #{applyType}
and APPLY_REF_ID  = #{applyId}
