select 

from t_repay_plan 
where  1=1

	and  app_id = #{appId,jdbcType=VARCHAR}


	and period >= #{period,jdbcType=INTEGER}

order by period

select 

from t_repay_plan
where 1=1

	and app_id = #{appId,jdbcType=VARCHAR}


	and period = #{period,jdbcType=INTEGER}

select
from t_repay_plan where APP_ID = #{appId} and
VALUE_DATE < #{curDate} and CLOSING_DATE>= #{curDate}

select * from t_repay_plan
where APP_ID = #{appId}


	and period >= #{beginPeriod} 




	and period <= #{endPeriod}


order by period asc
